import React, { Component } from 'react';
import './NewsZoom.css';
import { Container, Row, Col } from 'reactstrap';

class NewsZoom extends Component {

    render() { 
        return ( 
        <div>
            <Container fluid>
          <Row>

            <Col xs="8"><img  className="image1" src="https://i.imgur.com/x4gh62y.png" alt="touchdown"/><h3 className="flyM"><strong>Bobby McCain : l'homme volant !</strong></h3></Col>

            <Col xs="4" className="image2"><h5 className="news_title1"><strong>Latest News</strong></h5>
            <ul>
                <li>Huitième Touchdown de la saison pour Kenyan Drake</li>
                <li>Le Hard Rock Stadium fait peau neuve pour la nouvelle saison</li>
                <li>Albert Wilson, des performances en déclin</li>
                <li>Adam Gase, le nouveau coach !</li>
                <li>Une équipe prête au combat !</li>
            </ul>
          </Col>

            </Row>
        </Container>
        </div> );
    }
}

export default NewsZoom;
